    private void jButtonLoginActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButtonLoginActionPerformed



        Model.Usuario objLogin = new Model.Usuario();

        objLogin.setLogin(jTextFieldLogin.getText());
        objLogin.setSenha(jTextFieldSenha.getText());


        if (objLogin.login() > 0  ){
            
            Principal myFrame = new Principal();
            myFrame.setVisible(true);
            
            // jTextFieldLogin.setText("");
            // jTextFieldSenha.setText("");
        
            dispose();
            
            
        } else {
            JOptionPane.showMessageDialog(null, "Erro no login, verifique usuario e senha!.");
            jTextFieldSenha.grabFocus();
        }

        
       

        


        


        
       
    }//GEN-LAST:event_jButtonLoginActionPerformed

